# /// script\n# requires-python = \">=3.10\"\n# dependencies = [\n#     \"chromadb\",\n# ]\n# ///\n\"\"\"\nSimple script to check the contents of the ChromaDB collection.\n\"\"\"\n\nimport chromadb\nimport os\n\n# Configuration\nCHROMA_PATH = os.environ.get(\"CHROMA_PATH\", \"./database/chroma_db/\")\nCOLLECTION_NAME = \"conversations_dev\"\n\ndef main():\n    # Create persistent client\n    client = chromadb.PersistentClient(path=CHROMA_PATH)\n    \n    # Get collection\n    try:\n        collection = client.get_collection(COLLECTION_NAME)\n        print(f\"Collection '{COLLECTION_NAME}' exists.\")\n        \n        # Get count of documents\n        count = collection.count()\n        print(f\"Number of documents in collection: {count}\")\n        \n        # Get a few documents\n        if count > 0:\n            results = collection.get(limit=5)\n            print(\"\\nSample documents:\")\n            for i, (id, doc, meta) in enumerate(zip(results['ids'], results['documents'], results['metadatas'])):\n                print(f\"  {i+1}. ID: {id}\")\n                print(f\"     Document: {doc[:100]}...\")\n                print(f\"     Metadata: {meta}\")\n                print()\n        else:\n            print(\"No documents found in collection.\")\n            \n    except Exception as e:\n        print(f\"Error accessing collection: {e}\")\n\nif __name__ == \"__main__\":\n    main()